import React, { Component } from "react";
import { Line } from "draw-shape-reactjs";
import { Form, Input, Row, Button } from "antd";

class HexagonCluster extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startX: 100,
      startY: 100,
      hexagonName: "",
      hexagons: {},
    };
  }

  componentDidMount() {
    console.log("--current hexagon--", this.state.hexagons);
    Object.entries(this.state.hexagons).map((hexagon) => {
      console.log(hexagon, "---hexagon --");
      return this.createHexagon(hexagon[1], hexagon[0]);
    });
  }

  componentDidUpdate() {
    console.log("--current hexagon after update--", this.state.hexagons);
    Object.entries(this.state.hexagons).map((hexagon) => {
      console.log(hexagon, "---hexagon --");
      return this.createHexagon(hexagon[1], hexagon[0]);
    });
  }

  createHexagon = (hexagonBorders, hexagonName) => {
    return (
      <div key={hexagonName}>
        {hexagonBorders.map((border, index) => (
          <Line
            key={`${hexagonName}-${index}`}
            position="fixed"
            from={border.from}
            to={border.to}
            color={border.colour}
          />
        ))}
      </div>
    );
  };

  addHexagon = (event) => {
    // console.log(this.state.hexagonName, "=====hexagone name ----");
    // console.log(
    //   this.state.hexagonName,
    //   this.state.startX,
    //   this.state.startY,
    //   "----- hexagone details received ---"
    // );
    console.log(event, "----");
    // const { startX, startY, hexagonName } = this.state;
    // if (startX && startY && hexagonName) {
    //   const borders = [
    //     { from: [startX, startY], to: [startX + 50, startY], colour: "green" },
    //     { from: [startX + 50, startY], to: [startX + 100, startY + 50], colour: "red" },
    //     { from: [startX + 100, startY + 50], to: [startX + 50, startY + 100], colour: "yellow" },
    //     { from: [startX + 50, startY + 100], to: [startX, startY + 100], colour: "pink" },
    //     { from: [startX, startY + 100], to: [startX - 50, startY + 50], colour: "blue" },
    //     { from: [startX - 50, startY + 50], to: [startX, startY], colour: "violet" },
    //   ];
    //   console.log("-- updating cluster --");
    //   const newHexagon = { [hexagonName]: borders };
    //   this.setState((state) => ({ hexagons: { ...state.hexagons, ...newHexagon } }));
    // }
    // else {
    //   console.log("--hexagon details are required ---");
    // }
  };

  removeHexagon = () => {
    console.log("-- removing hexagon --", this.state.hexagonName);
  };

  createCluster() {
    return (
      <div key="villageCluster">
        {Object.entries(this.state.hexagons).map((hexagon) =>
          this.createHexagon(hexagon[1], hexagon[0])
        )}
      </div>
    );
  }

  render() {
    return (
      <div>
        <span style={{ color: "blue" }}> Hi There </span>
        {/* <Line key="0" position="fixed" from={[100, 100]} to={[150, 100]} color="green" />
        <Line key="1" position="fixed" from={[150, 100]} to={[200, 150]} color="red" />
        <Line key="2" position="fixed" from={[200, 150]} to={[150, 200]} color="yellow" />
        <Line key="3" position="fixed" from={[150, 200]} to={[100, 200]} color="pink" />
        <Line key="4" position="fixed" from={[100, 200]} to={[50, 150]} color="blue" />
        <Line key="5" position="fixed" from={[50, 150]} to={[100, 100]} color="violet" /> */}
        {/* {this.createHexagon({ startX: 100, startY: 100 })} */}

        {this.createCluster()}

        <Form layout="vertical" key="hexagonAddForm" onSubmit={this.addHexagon}>
          <Row>
            <Input placeholder="Name of hexagon" />
            <Input name="statX" placeholder="X coordinate" />
            <Input name="startY" placeholder="Y coordinate" />
            <Input type="submit" value="Submit">
              Add Hexagon
            </Input>
          </Row>
        </Form>

        <Form layout="vertical" key="hexagonRemoveForm" onSubmit={this.removeHexagon}>
          <Row>
            <Input placeholder="Name of hexagon" />
            <Input type="submit" value="Submit">
              Remove Hexagon
            </Input>
          </Row>
        </Form>
      </div>
    );
  }
}

export default HexagonCluster;
