import React, { Component } from "react";
import { Line } from "draw-shape-reactjs";
import { Form, Input, Row, Button } from "antd";

class HexagonCluster extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hexagons: [],
    };
  }

  createHexagon = (hexagon, hexagonIndex) => {
    return (
      <div key={hexagonIndex}>
        {hexagon.map((border, index) => (
          <Line
            key={`${hexagonIndex}-${index}`}
            position="fixed"
            from={border.from}
            to={border.to}
            color={border.colour}
          />
        ))}
      </div>
    );
  };

  addHexagon = ({ startX, startY }) => {
    const borders = [
      { from: [startX, startY], to: [startX + 50, startY], colour: "green" },
      { from: [startX + 50, startY], to: [startX + 100, startY + 50], colour: "red" },
      { from: [startX + 100, startY + 50], to: [startX + 50, startY + 100], colour: "yellow" },
      { from: [startX + 50, startY + 100], to: [startX, startY + 100], colour: "pink" },
      { from: [startX, startY + 100], to: [startX - 50, startY + 50], colour: "blue" },
      { from: [startX - 50, startY + 50], to: [startX, startY], colour: "violet" },
    ];
    console.log("-- updating cluster --");
    const newHexagon = borders;
    this.setState((state) => [...state.hexagons, newHexagon]);
  };

  createCluster() {
    return (
      <div key="villageCluster">
        {this.state.hexagons.map((hexagon, index) => this.createHexagon(hexagon, index))}
      </div>
    );
  }

  render() {
    return (
      <div>
        <span style={{ color: "blue" }}> Hi There </span>
        {/* <Line key="0" position="fixed" from={[100, 100]} to={[150, 100]} color="green" />
        <Line key="1" position="fixed" from={[150, 100]} to={[200, 150]} color="red" />
        <Line key="2" position="fixed" from={[200, 150]} to={[150, 200]} color="yellow" />
        <Line key="3" position="fixed" from={[150, 200]} to={[100, 200]} color="pink" />
        <Line key="4" position="fixed" from={[100, 200]} to={[50, 150]} color="blue" />
        <Line key="5" position="fixed" from={[50, 150]} to={[100, 100]} color="violet" /> */}
        {/* {this.createHexagon({ startX: 100, startY: 100 })} */}

        {this.createCluster()}

        <Form layout="vertical">
          <Row>
            <Input placeholder="Name of hexagon" />
            <Input.Group>
              <Input placeholder="X coordinate" />
              <Input placeholder="Y coordinate" />
            </Input.Group>
            <Button onClick={(name) => this.addHexagon(name, {})}>Add Hexagon</Button>
          </Row>

          <Row>
            <Input placeholder="Name of hexagon" />
            <Button>Remove Hexagon</Button>
          </Row>
        </Form>
      </div>
    );
  }
}

export default HexagonCluster;
