import React, { Component } from "react";
import { Line } from "draw-shape-reactjs";

// import { geometry, Image, Surface, Path, Text, Group } from "@progress/kendo-drawing";
// const { Rect, Point, Size, transform } = geometry;

class HexagonCluster extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // hexagons: [],
      border: {
        0: "green",
        1: "red",
        2: "yellow",
        3: "pink",
        4: "blue",
        5: "violet",
      },
    };
  }

  createHexagone = ({ startX, startY }) => {
    const borders = [
      { from: [startX, startY], to: [startX + 50, startY] },
      { from: [startX + 50, startY], to: [startX + 100, startY + 50] },
      { from: [startX + 100, startY + 50], to: [startX + 50, startY + 100] },
      { from: [startX + 50, startY + 100], to: [startX, startY + 100] },
      { from: [startX, startY + 100], to: [startX - 50, startY + 50] },
      { from: [startX - 50, startY + 50], to: [startX, startY] },
    ];
    return (<div>
      {borders.forEach((border) => <Line key="0" position="fixed" from={[100, 100]} to={[150, 100]} color="green" />)}
    </div>)
  };

  render() {
    return (
      <div id="line">
        Hi There
        <Line key="0" position="fixed" from={[100, 100]} to={[150, 100]} color="green" />
        <Line key="1" position="fixed" from={[150, 100]} to={[200, 150]} color="red" />
        <Line key="2" position="fixed" from={[200, 150]} to={[150, 200]} color="yellow" />
        <Line key="3" position="fixed" from={[150, 200]} to={[100, 200]} color="pink" />
        <Line key="4" position="fixed" from={[100, 200]} to={[50, 150]} color="blue" />
        <Line key="5" position="fixed" from={[50, 150]} to={[100, 100]} color="violet" />
      </div>
    );
  }
}

export default HexagonCluster;
